import { UserContext } from "../../components/contexts/user-context";
import { ProgramsContext } from "../../components/contexts/programs-context";
import NavBar from '../../components/navbar/Navbar';
import UserInfo from '../../components/user/UserInfo';

function ReactContext() {
    return (
        <div>
            <NavBar />
            <UserContext.Consumer>
                {stateData => {
                    const {username} = stateData.state;
                    return (
                        <div>
                            <em>Username: {username}</em>
                            <br />
                            <button onClick={() => stateData.actions.changeUsername('Khan')}>Change Username</button>
                        </div>
                    );
                }}
            </UserContext.Consumer>
            <ProgramsContext.Consumer>
                {stateData => {
                    const {title} = stateData.state;
                    return (
                        <div>
                            <em>Program title: {title}</em>
                        </div>
                    );
                }}
            </ProgramsContext.Consumer>
        </div>
    )
}

export default ReactContext
